page_titre = function(){
let exo=new createjs.Container()
let noir=new createjs.Shape();
noir.graphics.beginFill("#000000").drawRect(-5,-5,810,410)
exo.addChild(noir)
let zoom=new createjs.Shape();
zoom.graphics.beginFill("#FF0000").drawCircle(0,0,600)
zoom.x=400
zoom.y=200




//Création de la page question, exécutée à chaque question,
// tous les éléments de la page doivent être ajoutés à exo.blocAnimation
creerPageQuestion = function() { 
    let bg=new createjs.Container()
    bg.mask=zoom
    exo.addChild(bg) 

    let title = new  Titre();
    title.setTransform(367.6,105.4);

    let go = new  demarrer();
    go.setTransform(367,260.5);
    go.cursor="pointer";
    go.addEventListener("click",cparti)

   // Layer 3
    let animation =  la_scene();
    animation.setTransform(357.6,181.8);

   bg.addChild( animation,title,go);

    function cparti(){
        go.removeEventListener("click",cparti)
        createjs.Tween.get(go).to({scaleX:0,scaleY:0},100)
        createjs.Tween.get(title).to({alpha:0},300)
        createjs.Tween.get(animation.perso).to({x:animation.perso.ox},500).call(
            ()=>{
                let b=dialogue(titre,"qcm",["En avant !"],["#fc00ed"],["suite"]);
                b.x=640;
                b.y=80;            
                b.addEventListener("suite",()=>{
                    b.clear();
                    createjs.Tween.get(animation.perso).to({x:-90,y:300,scaleX:0.1,scaleY:0.1},3000).to({x:-120,y:175,scaleX:0.05,scaleY:0.05},3000).call(
                        ()=>{
                            createjs.Tween.get(zoom).to({scaleX:0,scaleY:0},1000).call(()=>{
                                exo.dispatchEvent("enroute")
                            }
                            )
                        }
                    )
                 })
                bg.addChild(b)

            })

    }
  
    
  
}


    
////////////////////////////////////// Bibliothèque annexe ///////////////////////////////////
Titre = function() {
    let res=new createjs.Container()

   // Layer 1
    let shape = new  createjs.Shape();
    shape.graphics.f("#FFFFFF").s().p("");
    shape.setTransform(-1.9,1.5);

   let shape_1 = new  createjs.Shape();
    shape_1.graphics.f("#CC3300").s().p("");
    shape_1.setTransform(2,-1.4);

   res.addChild( shape_1, shape);
   return(res)
}


indy = function() {
    let res=new createjs.Container()

   // Layer 1
    let instance = new createjs.Bitmap(images["indy"])
    instance.setTransform(119.2,-422.3,0.66,0.66,0,0,180);

    res.addChild( instance);
    return(res)
}

demarrer = function() {
    let res=new createjs.Container()

   // Layer 1
    let shape = new  createjs.Shape();
    shape.graphics.f().s("#CCCCCC").ss(6.9,1,1).p("AJGjVIyLAAQhiAAAABiIAADnQAABiBiAAISLAAQBiAAAAhiIAAjnQAAhihiAAg");

    let shape_1 = new  createjs.Shape();
    shape_1.graphics.f("#000000").s().p("");
    shape_1.setTransform(0,2);

    let shape_2 = new  createjs.Shape();
    shape_2.graphics.f("#FFFFFF").s().p("");

    res.addChild( shape_2, shape_1, shape);
    return(res)
}


la_scene = function() {
    let res=new createjs.Container()

   // Layer 1
    res.perso = new  indy();
    res.perso.setTransform(305.3,75.4,1,1,0,0,0,0,-211.1);
    res.perso.ox=res.perso.x;
    res.perso.oy=res.perso.y;
    res.perso.x=1000;

    let pyra = new  createjs.Bitmap(images["pyramide"]);
    pyra.setTransform(-444.1,11,0.31,0.31,-5.9);

    let fond= new  createjs.Bitmap(images["background"]);
    fond.setTransform(-413.3,-286.4,0.67,0.67);

    res.addChild( fond,pyra,res.perso);
    return(res)
}


/********************
*   C'est fini
*********************/
creerPageQuestion();
return exo;
};

